import React, { useState } from 'react';
function App() {
  const [inputList, setInputList] = useState('buy apple');
  const [items, setItems] = useState([]);
  function ItemEvent(e){
    setInputList(e.target.value);
  }
  function AddItem(items){
    setItems(...items,e.target.value);
  }
  return (
    <>

      <div className="card text-dark bg-light mb-3 center-div position-absolute top-50 start-50 translate-middle">
        <div className="card-header"><h1>Todo List</h1></div>
        <div className="card-body">
        <div className="input-group mb-3">

          <input type="text" className="form-control" placeholder="Add Item" aria-label="Username" aria-describedby="basic-addon1" onChange={ItemEvent}/>
          <span className="input-group-text" id="basic-addon1"><button type="submit" className="btn btn-lg" onClick={AddItem}> + </button></span>
          </div>
          <ul type="pl-0">

            {items.map(item => {
              return(
                <li className="list-group-item d-flex justify-content-between align-items-center">{item}<span className="badge bg-danger rounded-pill">X</span></li>
              ) 
            })}
          </ul>

        </div>
      </div>
    </>
  )
}

export default App
