import { useState } from 'react';
import './App.css';
import TodoForm from './components/TodoForm';

function App() {
  const [todos, settodos] = useState([]);
  function addTodo(todo){
    settodos([
      todo,
      ...todos,
    ])
  }

  return (
    <div className="App">
      <header className="App-header">
       <p>React todo</p>
       <TodoForm addTodo={addTodo}/>
      </header>
    </div>
  );
}

export default App;
